      });
   });
   //load home page
   var curpg = window.location.href.split('#')[1];
   var loadpg = curpg==undefined?'dashboard':curpg;
   page_lod(loadpg);

   $('div.main-menu ul.menu-list li a').click(function(){
      page_lod($(this).attr('href').split('#')[1]);
   });

   async function page_lod(page){
      $("#contents").empty();
      $('.breadcrumb-item').empty().html(page[0].toUpperCase() + page.slice(1));
      $('div.main-menu ul.menu-list li a').removeClass('active');
      $('div.main-menu ul.menu-list li a').each(function(){
         var $this = $(this);
         if($this.attr('href').indexOf(page) !== -1){
            $this.addClass('active');
         }
      });

      // const response = await fetch('userdata.json');
      // if (!response.ok) {
      //    const message = `An error has occured: ${response.status}`;
      //    throw new Error(message);
      // }
      // const post = await response.json();
      const post = 'from post';
      
      let myPromise = new Promise(function(resolve) {
         $("#contents").load(page+'.php', post, function (responseText, textStatus, req) {
            if(textStatus == "error") {
               page404();
            }
         });
      });

      let dd = await myPromise;
   }

   function page404(){
      if($('#contents').is(':empty')){
          $("#contents").empty().load('404.php');
      }
   }
