	function getAjaxCall(gaurl) {
		// console.log('getAjaxCall');
		return new Promise(function (resolve, reject) {
			$.get(gaurl, function(data, status){
				resolve(data);
			});
		});
	} 

	var promise = getAjaxCall(gaurl);  
	promise.then(function (values) {
		// console.log(values);
		var post = $.parseJSON(values);
		$("#contents").load('pages/'+loadpg+'.php', post, function (responseText, textStatus, req) {
